source1 = imread('face.tif');noise = imnoise(source1, 'gaussian', 0, 0.01);figure('name', '对比', 'NumberTitle', 'off', 'toolbar', 'none', 'menubar', 'none');subplot(5,3,2);imshow(noise);title('高斯噪声');gauss3 = fspecial('gaussian',[3,3]); G3 = uint8(filter2(gauss3, noise));gauss5 = fspecial('gaussian',[5,5]); G5 = uint8(filter2(gauss5, noise));gauss7 = fspecial('gaussian',[7,7]); G7 = uint8(filter2(gauss7, noise));gauss9 = fspecial('gaussian',[9,9]); G9 = uint8(filter2(gauss9, noise));w3 = double(ones(3)./9); Gav3 = uint8(filter2(w3, noise));w5 = double(ones(5)./25); Gav5 = uint8(filter2(w5, noise)); w7 = double(ones(7)./49); Gav7 = uint8(filter2(w7, noise));w9 = double(ones(9)./81); Gav9 = uint8(filter2(w9, noise)); med3 = medfilt2(noise, [3,3]); med5 = medfilt2(noise, [5,5]);med7 = medfilt2(noise, [7,7]);med9 = medfilt2(noise, [9,9]);subplot(5,3,4);imshow(G3);title('gauss3');subplot(5,3,5);imshow(Gav3);title('avg3');subplot(5,3,6);imshow(med3);title('med3');subplot(5,3,7);imshow(G5);title('gauss5');subplot(5,3,8);imshow(Gav5);title('avg5');subplot(5,3,9);imshow(med5);title('med5');subplot(5,3,10);imshow(G7);title('gauss7');subplot(5,3,11);imshow(Gav7);title('avg7');subplot(5,3,12);imshow(med7);title('med7');subplot(5,3,13);imshow(G9);title('gauss9');subplot(5,3,14);imshow(Gav9);title('avg9');subplot(5,3,15);imshow(med9);title('med9');